import styles from "../NavBar/NavBar.module.css"
import {motion} from 'framer-motion'
import { useDispatch, useSelector } from "react-redux";

import LightLogo from '../../Assets/Logo/LogoLight.png'
import { changeMode } from "../../Redux/Actions";
function NavBar() {
    const dispatch = useDispatch()
    const Dark = useSelector((state)=>state.dark)
    console.log(Dark);
    const containerVariants={
        hidden:{x:-2000},
        visible:{x:0,
        transition:{
            duration:1
        }}
    }
    const handleDark = (e) =>{
        e.preventDefault()
        dispatch(changeMode())
    }
  return (
    <div className={styles.Container}>
            <motion.div  
                className={styles.LinksContainer}
                initial= "hidden"
                animate="visible"
                variants={containerVariants}>
                    <img src={LightLogo} alt="" className={styles.logo}></img>
                    <p className={styles.Link}>Inicio</p>
                    <p className={styles.Link}>Sobre mí</p>
                    <p className={styles.Link}>Tecnologias</p>
                    <p className={styles.Link}>Proyectos</p>
                    <p className={styles.Link}>Contacto</p>
                    <div onClick={handleDark}>
                        <label className={styles.switchContainer} >
                        <input type="checkbox" />
                        <span className={styles.slider} ></span>
                        </label> 
                    </div>
                    <p className={styles.Link}>EN</p>
                    <p className={styles.Link}>ES</p>
            </motion.div >
    </div>
  );
}

export default NavBar;
