import { CHANGE_MODE } from "./Actions";

let initialState = {
  dark: false,
};

export default function rootReducer(state = initialState, action) {
  switch (action.type) {
    case CHANGE_MODE:
      if (dark === false) {
        return {
          ...state,
          dark: true,
        };
      } else {
        return {
          ...state,
          dark: false,
        };
      }
    default:
      return {
        ...state,
      };
  }
}
