import { CHANGE_MODE, CHANGE_TO_ENGLISH } from "./Actions";

let initialState = {
  dark: false,
  english: false,
};

export default function rootReducer(state = initialState, action) {
  switch (action.type) {
    case CHANGE_MODE:
      if (state.dark === false) {
        return {
          ...state,
          dark: true,
        };
      } else {
        return {
          ...state,
          dark: false,
        };
      }
    case CHANGE_TO_ENGLISH: {
      return {
        ...state,
        english: true,
      };
    }

    default:
      return {
        ...state,
      };
  }
}
